import logo from './logo.svg';
import React from "react";
import './App.css';
import {
  Dropdown,
  DropdownButton,
  Container,
  Row,
  Col,
  Modal,
  Button
} from "react-bootstrap";
import axios from "axios";


const apiUrl="http://ec2-3-88-109-250.compute-1.amazonaws.com/api"


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searched:'',
      title:'', 
      photo:'',
      description:'',
      short_description:''
    }
    this.lookForCar = this.lookForCar.bind(this);
    this.handleKeyPress= this.handleKeyPress.bind(this);
  }


  handleKeyPress (e) {
    console.log(e.keyCode)
    //it triggers by pressing the enter key
  if (e.keyCode === 13) {
    this.lookForCar();
  }
};


  lookForCar(){
    var inputVal = document.getElementById("search"); 
      if(inputVal != null){
        axios.get(apiUrl+"/search", {headers:{ Title: inputVal.value}})
        .then(data =>{console.log(data.data.data[0])
        this.setState({ 
          title:  data.data.data[0].Title, 
          photo:  data.data.data[0].Photo, 
          description:  data.data.data[0].Description, 
          short_description:  data.data.data[0].Short_Description, 
        }) } )




        var doc = document.getElementById(inputVal.value);





        if (doc !== null) {
         doc.classList.add("blink");
         setTimeout(function(){
          doc.classList.remove("blink");
       }, 5000);
        }
      }
  }




render(){
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>


        <form className="commentForm" onSubmit={this.lookForCar}>
        <input id="search" class="form-control mr-sm-2 auxNig" type="text"  placeholder="Search for a Car"    onKeyPress={this.lookForCar} />    
        <Button  id="submit" onClick={this.lookForCar}></Button>
       </form>

        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
         {this.state.title}
         <img src={this.state.photo} alt="Test"/> 
         {this.state.description}
         {this.state.short_description}
        </a>
      </header>
    </div>
  );}
}

export default App;
