import logo from './logo.svg';
import React from "react";
import './App.css';
import Car from './Car.js';
import {
  Dropdown,
  DropdownButton,
  Container,
  Row,
  Col,
  Modal,
  Button
} from "react-bootstrap";
import axios from "axios";
const apiUrl="http://ec2-3-88-109-250.compute-1.amazonaws.com/api"
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = JSON.parse(window.localStorage.getItem('state')) || {
      searched:'',
      title:'', 
      photo:'',
      description:'',
      short_description:''
    }
    this.lookForCar = this.lookForCar.bind(this);
    this.goTo= this.goTo.bind(this);
    this.reload= this.reload.bind(this);
  }


  reload() {
    this.setState({ 
      title:  '', 
      photo:  '', 
      description:  '', 
      short_description:  '', 
    })
  }

  setState(state) {
    window.localStorage.setItem('state', JSON.stringify(state));
    super.setState(state);
  }

  goTo () {
    console.log("Go to")
    this.props.history.push(   '/Car',{ title:this.state.title ,photo:this.state.photo,description:   this.state.description,short_description:  this.state.short_description});
  }



onKeyDown = (event: React.KeyboardEvent<HTMLDivElement>): void => {
  // 'keypress' event misbehaves on mobile so we track 'Enter' key via 'keydown' event
  if (event.key === 'Enter') {
    event.preventDefault();
    event.stopPropagation();
    this.lookForCar();
  }
}


  lookForCar(){
    var inputVal = document.getElementById("search"); 
      if(inputVal != null){
        axios.get(apiUrl+"/search", {headers:{ Title: inputVal.value}})
        .then(data =>{console.log(data.data.data[0])
        this.setState({ 
          title:  data.data.data[0].Title, 
          photo:  data.data.data[0].Photo, 
          description:  data.data.data[0].Description, 
          short_description:  data.data.data[0].Short_Description, 
        })
        localStorage.setItem('title_test', data.data.data[0].Title);


      } )




        var doc = document.getElementById(inputVal.value);





        if (doc !== null) {
         doc.classList.add("blink");
         setTimeout(function(){
          doc.classList.remove("blink");
       }, 5000);
        }
      }
  }




render(){
  return (
    <div className="App">
      <header className="App-header">
      <button type="button" className="btn btn-success" onClick={this.reload}>Home</button>
        <img src={logo} className="App-logo" alt="logo" />
        <p>
        <form className="commentForm">
           <input
             id="search"
             className="comment-input"
             aria-multiline="true"
             role="textbox"
             contentEditable={true}
             onKeyDown={this.onKeyDown}
             ref={node => this.input = node} 
           />
           <button type="button" className="btn btn-success" onClick={this.lookForCar}>Comment</button>
         </form>
        </p>

         {this.state.title}
         <img src={this.state.photo} alt="Test"/> 
         {this.state.description}
         {this.state.short_description}
         <Button onClick={this.goTo}></Button>

      </header>
    </div>
  );}
}

export default App;
